const createRandomString = function (strLength) {
  strLength = typeof strLength == "number" && strLength > 0 ? strLength : false;
  if (strLength) {
    var possibleCharacters = "abcdefghijklmnopqrstuvwxyz0123456789";

    var str = "";
    for (var i = 1; i <= strLength; i++) {
      var randomCharacter = possibleCharacters.charAt(
        Math.floor(Math.random() * possibleCharacters.length)
      );
      str += randomCharacter;
    }
    return str;
  } else {
    return false;
  }
};
if (JSON.parse(localStorage.getItem("employeeList")) == undefined) {
  localStorage.setItem("emploeeList", [
    { id: -1, empName: "Hey There employee name here...!" },
  ]);
}
var initalState = JSON.parse(localStorage.getItem("employeeList"));

const EmployeesReducer = (state = initalState, action) => {
  const { type, payload } = action;

  switch (type) {
    case "CREATE_EMPLOYEE":
      return [
        ...state,
        {
          id: createRandomString(16),
          empName: payload.empName,
          empRole: payload.empRole,
        },
      ];
    case "DELETE_EMPLOYEE":
      const copyState = [...state];
      //Find id of object to remove
      const i = copyState.findIndex((x) => x.id === payload.id);
      copyState.splice(i, 1);
      return [...copyState];
    default:
      return state;
  }
};

export default EmployeesReducer;
