import React, { useState } from "react";
import { useDispatch } from "react-redux";
import Select from "react-select";

const createRandomString = function (strLength) {
  strLength = typeof strLength == "number" && strLength > 0 ? strLength : false;
  if (strLength) {
    var possibleCharacters = "abcdefghijklmnopqrstuvwxyz0123456789";

    var str = "";
    for (var i = 1; i <= strLength; i++) {
      var randomCharacter = possibleCharacters.charAt(
        Math.floor(Math.random() * possibleCharacters.length)
      );
      str += randomCharacter;
    }
    return str;
  } else {
    return false;
  }
};
const options = [
  { value: "web developer", label: "Web Developer" },
  { value: "intern", label: "Intern" },
  { value: "admin", label: "Admin" },
];

export default function PopUp(props) {
  const dispatch = useDispatch();
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleChanges = (options) => {
    setSelectedOptions(options);
  };
  console.log(selectedOptions);
  const [data, setData] = useState({
    empName: props.data
      ? props.data.empName
      : "Hey there enter Employee name here...!",
    empRole: props.data
      ? [props.data.empRole]
      : "Hey there select Employee Role here...!",
  });
  const handleChange = (e) => {
    e.persist();
    setData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const addEmployee = () => {
    dispatch({
      type: "CREATE_EMPLOYEE",
      payload: {
        id: createRandomString(16),
        empName: data.empName,
        empRole: data.empRole,
      },
    });
  };

  const updateEmployee = () => {
    dispatch({
      type: "UPDATE_EMPLOYEE",
      payload: {
        id: props.data.empId,
        empName: data.empName,
        empRole: data.empRole,
      },
    });
  };

  return (
    <div id="mypopup" className="popup">
      <div className="popup-content">
        <span className="close" onClick={props.close}>
          &times;
        </span>

        <div className="addForm ">
          <div className="row">
            <div className="hello-1">
              <label htmlFor="empName">Employee Name</label>
            </div>
            <div className="hello-2">
              <input
                type="text"
                id="empName"
                name="empName"
                onFocus={(e) =>
                  props.data
                    ? (e.target.value = data.empName)
                    : (e.target.value = "")
                }
                value={data.empName}
                placeholder="Add an Employee here...!"
                onChange={(e) => handleChange(e)}
              />
            </div>
          </div>
          <div className="row">
            <div className="hello-1">
              <label htmlFor="empRole">Employee Role</label>
            </div>
            <div className="hello-2">
              {/* <select value={data.empRole} onChange={(e) => handleChange(e)}>
                <option value="australia">Australia</option>
                <option value="canada">Canada</option>
                <option value="usa">USA</option>
              </select> */}
              <Select
                id="empRole"
                name="empRole"
                closeMenuOnSelect={false}
                defaultValue={[options[0], options[1]]}
                isMulti
                options={options}
                onChange={handleChanges}
              />
            </div>
          </div>

          <div className="row">
            <button
              className="addButton"
              onClick={props.data ? updateEmployee : addEmployee}
            >
              {props.data ? "Update" : "Add"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
