if (localStorage.getItem("employeeList") == null)
  localStorage.setItem("employeeList", JSON.stringify([]));
let initialState = {
  currentIndex: -1,
  list: JSON.parse(localStorage.getItem("employeeList")),
};

const EmployeesReducer = (state = initialState, action) => {
  const { type, payload } = action;
  var list = JSON.parse(localStorage.getItem("employeeList"));

  /* switch (type) {
    case "CREATE_EMPLOYEE":
      state = localStorage.setItem("emploeeList", [
        {
          id: createRandomString(16),
          empName: payload.empName,
          empRole: payload.empRole,
        },
      ]);

    case "DELETE_EMPLOYEE":
      const copyState = [...state];
      //Find id of object to remove
      const i = copyState.findIndex((x) => x.id === payload.id);
      copyState.splice(i, 1);
      return [...copyState];
    default:
      return state;
  } */
  switch (type) {
    case "CREATE_EMPLOYEE":
      list.push(payload);
      localStorage.setItem("employeeList", JSON.stringify(list));
      return { list };

    case "UPDATE":
      list[state.currentIndex] = action.payload;
      localStorage.setItem("transactions", JSON.stringify(list));
      return { list };

    case "DELETE_EMPLOYEE":
      const i = list.findIndex((x) => x.id === payload.id);
      list.splice(i, 1);
      localStorage.setItem("employeeList", JSON.stringify(list));
      return { list, currentIndex: -1 };
    default:
      return state;
  }
};

export default EmployeesReducer;
