import React, { useState } from "react";
import { useDispatch } from "react-redux";
import PopUp from "./Utils/PopUp";
import { Link } from "react-router-dom";

const Activity = (props) => {
  const [update, setUpdate] = useState(false);

  const close = () => {
    setUpdate(false);
  };

  const dispatch = useDispatch();
  const deleteEmployee = (id) => {
    dispatch({
      type: "DELETE_EMPLOYEE",
      payload: {
        id: id,
      },
    });
  };

  /*
   */
  return (
    <div style={{ overflowX: "auto", marginTop: "7vh" }}>
      <h2 style={{ textAlign: "center", marginTop: "4vh" }}>Employee List</h2>
      <table>
        <tr>
          <th>Employee Name</th>
          <th>Employee Roles</th>
          <th>Update Employee</th>
          <th>Delete Employee</th>
        </tr>
        {props.list.map((employee) => (
          <tr>
            <td style={{ fontWeight: "630" }}>
              <Link
                style={{ textDecoration: "none", cursor: "pointer" }}
                to={`/employee/${employee.empName}`}
              >
                {employee.empName}
              </Link>
            </td>
            <td>
              {Array.prototype.map
                .call(employee.empRole, function (item) {
                  return item.label;
                })
                .join(", ")}
            </td>
            <td>
              <button onClick={() => deleteEmployee(employee.id)}>
                Delete
              </button>
            </td>
            <td>
              <button onClick={() => setUpdate(!update)}>Update</button>
            </td>
            {update && (
              <PopUp
                data={{
                  empName: employee.empName,
                  empRole: employee.empRole,
                  empId: employee.id,
                }}
                close={close}
              />
            )}
          </tr>
        ))}
      </table>
    </div>
  );
};

export default Activity;
