import React, { useState } from "react";
import { useDispatch } from "react-redux";

export default function PopUp(props) {
  const dispatch = useDispatch();
  const [data, setData] = useState({
    empName: "",
    empRole: "",
  });
  const handleChange = (e) => {
    e.persist();
    setData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const addEmployee = () => {
    dispatch({
      type: "CREATE_EMPLOYEE",
      payload: {
        empName: data.empName,
        empRole: data.empRole,
      },
    });
  };
  return (
    <div id="mypopup" class="popup">
      <div className="popup-content">
        <span className="close" onClick={props.close}>
          &times;
        </span>

        <div className="addForm ">
          <div className="row">
            <div className="hello-1">
              <label for="empName">Employee Name</label>
            </div>
            <div className="hello-2">
              <input
                onChange={(e) => handleChange(e)}
                type="text"
                id="empName"
                name="empName"
                placeholder="Add an Employee here...!"
              />
            </div>
          </div>
          <div className="row">
            <div className="hello-1">
              <label for="empRole">Employee Role</label>
            </div>
            <div className="hello-2">
              <select
                id="country"
                name="empRole"
                onChange={(e) => handleChange(e)}
              >
                <option value="australia">Australia</option>
                <option value="canada">Canada</option>
                <option value="usa">USA</option>
              </select>
            </div>
          </div>

          <div className="row">
            <button
              className="addButton"
              onClick={(() => addEmployee, props.close)}
            >
              Add
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
